import React from "react";

class DigitalTime extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      time: "Hell",
    };
  }

  componentDidMount = () => {
    setInterval(() => {
      this.setState({time: new Date().toLocaleTimeString});
    }, [1000]);
  };

  render() {
    return (
      <>
        <h1>Digital Time: {this.state.time}</h1>
      </>
    );
  }
}

export default DigitalTime;
