import React from "react";

class DigitalTime extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      time: new Date(),
    };
  }

  componentDidMount = () => {
    setInterval(() => {
      this.setState({time: this.state.time});
    });
  };

  render() {
    return (
      <>
        <h1>{this.state.time}</h1>
      </>
    );
  }
}

export default DigitalTime;
