import React from "react";

class DigitalTime extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      time: new Date().toLocaleTimeString(),
      date: new Date().toDateString(),
    };
  }

  componentDidMount = () => {
    setInterval(() => {
      this.setState({time: new Date().toLocaleTimeString()});
    }, 1000);

    setInterval(() => {
      this.setState({date: new Date().toLocaleDateString});
    }, 1000);
  };

  render() {
    return (
      <>
        <h1>Digital Time: {this.state.time}</h1>
        <h1>Digital Time: {this.state.date}</h1>
      </>
    );
  }
}

export default DigitalTime;
